import React from "react";
import "./style.css";
import { useState } from "react";

function App() {
  const [bgColor, setBgColor] = useState("white");

  function changeBGColor() {
    if (bgColor === "black") {
      setBgColor("white");
    } else {
      setBgColor("black");
    }
  }

  return (
    <div>
      <h1>Hello World</h1>
      <button style={{ backgroundColor: bgColor }} onClick={changeBGColor}>
        turn the light off{" "}
      </button>
    </div>
  );
}

export default App;
