  const [bgColor , setBgColor] = useState("yellow");

  function changeBGColor() {
    if(bgColor === "blue"){
    setBgColor("yellow")
  }else {
    setBgColor("blue");
  }}
  return (
    <div style = {{backgroundColor: bgColor}} > 
      <h1>Hello World</h1>
     <button style={{backgroundColor: bgColor}} onClick={changeBGColor}>turn the light on </button>


    </div>
  );
}

export default App;
