    function ChangeColor() {
        if (lightColor ===true) {
            setBgColor ("black")
        }else {
            darkColor("yellow") }
    }
    return (
        <div>
            <h1>Hello World</h1>
            <button onClick = {{backgroundColor:ChangeColor}}>turn the light on </button>
        </div>
    )
}

export default App;
