import React from "react";
import "./style.css";
import { useState } from "react";

function App() {
  const [lightColor, setBgColor] = useState("yellow");

  function ChangeColor() {
    if (lightColor == "black") {
      setBgColor("black");
    } else {
      setBgColor("yellow");
    }
  }
  return (
    <div>
      <h1>Hello World</h1>
      <button style={{backgroundColor:lightColor}} onClick={ChangeColor}>turn the light on{ChangeColor}</button>



    </div>
  );
}

export default App;
